    private static void getUnOrderedList(List<Integer> list) {
        for(int i=0;i<100000;i++){
            int newElement=(int)(Math.random()*100);
            Ekle(list,newElement);
        }
    }

    private static boolean Ekle(List<Integer> list, int newElement) {
        ListIterator<Integer>listIterator01=list.listIterator();
        while (listIterator01.hasNext()){
            listIterator01.add(newElement);
            return true;//methodtan cıksın diye
        }
        listIterator01.add(newElement);return true;
        //ilk eklemede while false'tur
        //ilk eklemeyi yaptım ve methodtan cıktım
    }


    public static void getList(List<Integer> list) {
        for(int i=0;i<1000;i++){
            int newElement=(int)(Math.random()*100);
            sıralıEkle(list,newElement);
        }
    }

    private static boolean sıralıEkle(List<Integer> list, int newElement)
    {
        ListIterator<Integer>listIterator01=list.listIterator();
        while (listIterator01.hasNext()){
            int comparingResult=listIterator01.next().compareTo(newElement);
            //listIterator01 ın basındayım .ilerigit ve sayımı oku
            // .newelementlekıyasla(newelement)
            if(comparingResult==0)
            {
                listIterator01.add(newElement);
                return true;//methodtan cıksın diye
            }
            else if(comparingResult>0) //newElement küçükse
            {
                listIterator01.previous();//bir adım geri at
                listIterator01.add(newElement);
                return true;
            }
            else {//element büyükse
                //do nothing
                //let iterator step forward, comparingResult will be 0 or pozitive
                //listIterator01.add(newElement);
            }
        }
        listIterator01.add(newElement);//while(false) olucak il eleman eklerken oyuzden yazdım
        return true;//calıstı
    }
